mapper.insert:
INSERT into workTask(workType,workDate,workItem,unitPrice,quantity,memo)
VALUES(#{workType},#{workDate},#{workItem},#{unitPrice},#{quantity},#{memo})
on DUPLICATE key UPDATE workType=#{workType},unitPrice=#{unitPrice},quantity=#{quantity},memo=#{memo}

mapper.select:
SELECT workDate,max(workType) as workType,count(1) as items,sum(unitPrice*quantity) as money from workTask
and workDate >= #{param.stDate}
and workDate <= #{param.enDate}
and workItem like concat('%',#{param.workItem},'%')
GROUP BY workDate order by workDate desc
